        public List<string[]> AnalogyPairsList() {
            //Vocabulary analogyVocab = new Vocabulary(null, vocab.VectorDimensionsCount);

            var pairs = new List<string[]>(); // list of pairs of strings

            //foreach (string line in analogies.Split(new char[] { '\n', '\r' })) {

            var reader = new StringReader(analogies);
            int lineNumber = 0;
            string line;
            while ((line = reader.ReadLine()) != null) {
                lineNumber++;

                if (string.IsNullOrWhiteSpace(line))
                    continue;

                // remove comments
                if (line.Contains("//")) {
                    line = line.Substring(0, line.IndexOf("//"));
                }
                line = line.Trim();
                if (string.IsNullOrEmpty(line))
                    continue;

                line = line.ToLowerInvariant();

                string[] pair = null;
                foreach (var joiner in joiners) {
                    if (line.Contains(joiner)) {
                        pair = line.Split(new string[] { joiner }, StringSplitOptions.RemoveEmptyEntries);
                        break;
                    }
                }

                if (pair == null || pair.Length != 2)
                    continue;

                if (pairs.Contains(pair))
                    continue;

                pair[0] = pair[0].Trim();
                pair[1] = pair[1].Trim();
                pairs.Add(pair);
                pairs.Add(new string[] { pair[1], pair[0] });
            }

            return pairs;
        }

